import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;
  const gradeint = props.gradeint;


// text button function
  function textButton({ props }) {
    return (
      <TXButton
        color={props.color}
        gradeint={props.gradeint}
        text={props.label}
      >
        {props.prefix ? <FontAwesomeIcon icon={props.prefix} /> : ""}
        <p>{props.label}</p>
        {props.suffix ? <FontAwesomeIcon icon={props.suffix} /> : ""}
      </TXButton>
    );
  }


//   const options = Document.getElementById("select").children;
//   console.log(options)


//select component function
function  selectButton({props}){
}


//   generate origin component by components props
  function checkComponents() {
    if (component === "text") {
      return textButton({ props });
    }else if(component === "select"){
        return selectButton({props});
    }
  }

  return checkComponents();
}


// text button styled 
const TXButton = styled.div`
  border: 1px solid ${(props) => (props.color ? props.color : "none")};
  border-radius: 20px;
  padding: ${(props) =>
    props.text === "لغو" ? "4px 70px 0px" : "4px 30px 0px"};
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 20px;
  width: fit-content;
  background: ${(props) =>
    props.gradeint
      ? `transparent linear-gradient(90deg, ${props.gradeint.slice(
          0,
          7
        )} 0%, ${props.gradeint.slice(8, 15)} 100%) 0% 0% no-repeat padding-box`
      : ""};

  p {
    font-size: 24px;
    font-family: YekanBakh;
    color: ${(props) => (props.color ? props.color : "white")};
    margin: 0;
    font-weight: 500;
  }

  svg {
    color: ${(props) => (props.color ? props.color : "white")};
  }
`;


// select component styled
export const Option = styled.div`
`
