import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;

  function textButton({ label, color, suffix, prefix }) {
    return (
      <TXButton color={color}>
        {prefix ? <FontAwesomeIcon icon={prefix} /> : ""}
        <p>{label}</p>
        {suffix ? <FontAwesomeIcon icon={suffix } /> : ""}
      </TXButton>
    );
  }

  function checkComponents(component, label, color,suffix) {
    if (component === "text") {
      return textButton({ label, color,suffix });
    }
  }

  return checkComponents(component, label, color,suffix);
}

const TXButton = styled.div`
  border: 1px solid ${(props) => props.color};
  border-radius: 20px;
  padding: 4px 30px;
  display: flex;
  flex-direction: row;
  align-items: baseline;
  gap: 20px;

  p {
    font-size: 24px;
    font-family: YekanBakh;
    color: ${(props) => props.color};
    margin: 0;
    font-weight: 500;
  }

  svg{
    color: ${(props) => props.color};
  }

`;
