import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;
  const gradeint = props.gradeint;
  const [options,setOptions] = useState([]);

  useEffect(()=>{
    if(component === "select"){
        setOptions([...options, props.children]);
    }
  },[])


  // text button function
  function textButton({ props }) {
    return (
      <TXButton
        color={props.color}
        gradeint={props.gradeint}
        text={props.label}
      >
        {props.prefix ? <FontAwesomeIcon icon={props.prefix} /> : ""}
        <p>{props.label}</p>
        {props.suffix ? <FontAwesomeIcon icon={props.suffix} /> : ""}
      </TXButton>
    );
  }



  //select component function
  function SelectButton({ props }) {
    const [open, setOpen] = useState(false);
    const [option, setOption] = useState(props.defaultValue);
    let getOptions = [];
    options.map(x=>{
        for(let i=0 ; i< x.length ; i++){
            getOptions.push(x[i]);
        }
    })



    const handleClick = () => {
      if (!open) {
        setOpen(true);
      } else {
        setOpen(false);
      }
    };

    const handleText = (ev) => {
      setOption(ev.currentTarget.textContent);
    };

    const itemList = (props) => {
      const list = props.map((item) => (

        <div
          onClick={handleText}
          className="dropdown__item"
           key={item.toString()}
        >
          {item}
        </div>

      ) );

      return <div className="dropdown__items"> {list} </div>;
    };

    return (
      <DropDown className={open ? "active" : ""} onClick={handleClick} >
        <div className="dropdown__text">
          {typeof option === 'string' ? option :  getOptions[option-1]}
          <span className="arrow"></span>
        </div>
        {itemList(getOptions)}
      </DropDown>
    );
  }

  //radio button function
  function RadioButton({props}){
    if(props.type === "square"){
        return (
            <FirstRadioButton>
                <span></span>
                <p>{props.label}</p>
            </FirstRadioButton>
        )
    }else{
        return (
            <SecondRadioButton>
                <span></span>
                <p>{props.label}</p>
            </SecondRadioButton>
        )
    }
  }

  //   generate origin component by components props
  function checkComponents() {
    if (component === "text") {
      return textButton({ props });
    } else if (component === "select") {
      return SelectButton({ props });
    }else if( component === "radioGroup"){
        return RadioButton({props}) ;
    }
  }

  return checkComponents();
}

// text button styled
const TXButton = styled.div`
  border: 1px solid ${(props) => (props.color ? props.color : "none")};
  border-radius: 20px;
  padding: ${(props) =>
    props.text === "لغو" ? "4px 70px 0px" : "4px 30px 0px"};
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 20px;
  width: fit-content;
  background: ${(props) =>
    props.gradeint
      ? `transparent linear-gradient(90deg, ${props.gradeint.slice(
          0,
          7
        )} 0%, ${props.gradeint.slice(8, 15)} 100%) 0% 0% no-repeat padding-box`
      : ""};

  p {
    font-size: 24px;
    font-family: YekanBakh;
    color: ${(props) => (props.color ? props.color : "white")};
    margin: 0;
    font-weight: 500;
  }

  svg {
    color: ${(props) => (props.color ? props.color : "white")};
  }
`;

// select component styled
export const Option = styled.div``;

const DropDown = styled.div`
  position: relative;
  background: #f5f5f5 0% 0% no-repeat padding-box;
  border: 1px solid #37b3b8;
  border-radius: 18px;
  padding: 2.5px 0 2.5px 0;

  .dropdown__text {
    color: #37b3b8;
    cursor: pointer;
    font-family: YekanBakh;
    font-size: 20px;
    text-align: end;
    position: relative;
    padding-right:25px;
    padding-left:10px;
    min-width:160px;
  }

  .arrow {
    width: 20px;
    height: 10px;
    display: inline-block;
    position: absolute;
    margin-right:35px;
    bottom: 5px;
    left: 4px;
    transition: 0.4s ease;
    margin-top: 2px;
    text-align: left;
    transform: rotate(45deg);
    float: left;
    &:before,
    &:after {
      position: absolute;
      content: "";
      display: inline-block;
      width: 12px;
      height: 2px;
      background-color: #37B3B8;
      transition: 0.4s ease;
    }
    &:after {
      position: absolute;
      transform: rotate(90deg);
      top: -5px;
      left: 5px;
    }
  }

  &.active{
    .arrow{
        transform: rotate(45deg) translate(-5px,-5px);
        &:before{
            transform: translate(10px,0);
        }
        &:after{
            transform: rotate(90deg) translate(10px,0);
        }
    }
  }


  .dropdown__items {
    position: relative;
    visibility: hidden;
    opacity: 0;
    max-height: 0px;
    transition: max-height 0.6s ease;
  }

  &.active {
    .dropdown__items {
      visibility: visible;
      height: auto;
      max-height: 290px;
      opacity: 1;
      transition: max-height 0.7s, opacity 3s, visibility 4s ease;
    }
  }

  .dropdown__item {
    cursor: pointer;
    padding-right: 25px ;
    padding-left:10px;
    color: #37b3b8;
    font-family: YekanBakh;
    font-size: 20px;
    text-align: end;
    border-top:1px solid #FFFFFF;
    font-weight:300;

  }


`;


//radio button styled 
const FirstRadioButton = styled.div`

`;

const SecondRadioButton = styled.div`
`
