import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { upload } from '@fortawesome/fa-solid fa-upload'



export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;

  function textButton({ label,color }) {
    return (
      <TXButton color={color}>
        {/* <span className="prefix"></span> */}
        <p >{label}</p>
        {/* <span className="suffix"></span> */}
        <FontAwesomeIcon icon={upload} />
      </TXButton>
    );
  }

  function checkComponents(component, label,color) {
    if (component === "text") {
      return textButton({ label ,color});
    }
  }

  return checkComponents(component, label ,color);
}

const TXButton = styled.div`
  border: 1px solid ${props => props.color};
  border-radius: 20px;
  padding:4px 30px ;

  p{
    font-size:24px;
    font-family:YekanBakh;
    color:${props=>props.color};
    margin:0;
    font-weight:500;
  }

  .prefix{

  }
  .suffix{

  }

`;
