import React, { useEffect, useState } from "react";
import styled from "styled-components";
import DatePicker from "react-multi-date-picker";
import persian from "react-date-object/calendars/persian";
import persian_fa from "react-date-object/locales/persian_fa";
import InputIcon from "react-multi-date-picker/components/input_icon";
import TextButton from "./textButton/indx";
import SelectButton from "./selectButton";

export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;
  const gradeint = props.gradeint;
  const [options, setOptions] = useState([]);

  useEffect(() => {
    if (component === "select") {
      setOptions([...options, props.children]);
    }
  }, []);


//   //select component function
//   function SelectButton({ props }) {









//   }

  //radio button function
  function RadioButton({ props }) {
    const [active, setActive] = useState(false);
    function handleClick() {
      if (active) {
        setActive(false);
      } else {
        setActive(true);
      }
    }

    if (props.type === "square") {
      return (
        <FirstRadioButton
          className={active ? "active" : ""}
          onClick={handleClick}
        >
          <div className="checkbox">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="white"
              class="bi bi-check2"
              viewBox="0 0 16 16"
            >
              <path d="M13.854 3.646a.5.5 0 0 1 0 .708l-7 7a.5.5 0 0 1-.708 0l-3.5-3.5a.5.5 0 1 1 .708-.708L6.5 10.293l6.646-6.647a.5.5 0 0 1 .708 0z" />
            </svg>
          </div>
          <span className="circle"></span>
          <p className="content">{props.label}</p>
        </FirstRadioButton>
      );
    } else {
      return (
        <SecondRadioButton
          className={active ? "active" : ""}
          onClick={handleClick}
        >
          <div className="checkbox"></div>
          <p className="content">{props.label}</p>
        </SecondRadioButton>
      );
    }
  }

  //   date input function
  function DateInput({ props }) {
    const weekDays = [
      "شنبه",
      "یکشنبه",
      "دوشنبه",
      "سه شنبه",
      "چهارشنبه",
      "پنچشنبه",
      "جمعه",
    ];
    let today = new Date();

    let comma = document.getElementsByClassName("rmdp-header-values");
    console.log("this", comma);
    return (
      <PersianDatePicker>
        <DatePicker
          calendar={persian}
          locale={persian_fa}
          value={props.defaultValue ? props.defaultValue : today}
          render={<InputIcon />}
          weekDays={weekDays}
          arrow={false}
        />
      </PersianDatePicker>
    );
  }

  //   generate origin component by components props
  function checkComponents() {
    if (component === "text") {
      return <TextButton props={props} />;
    } else if (component === "select") {
      return <SelectButton props={props}  options={options}/>;
    } else if (component === "radioGroup") {
      return RadioButton({ props });
    } else if (component === "date") {
      return DateInput({ props });
    }
  }

  return checkComponents();
}



//radio button styled
const FirstRadioButton = styled.div`
  display: flex;
  flex-direction: row-reverse;
  justify-content: center;
  align-items: center;
  gap: 12px;
  cursor: pointer;
  position: relative;
  .checkbox {
    border: 1px solid #6e6d6d;
    width: 20px;
    height: 20px;
    border-radius: 5px;
    display: flex;
    svg {
      margin: auto;
    }
  }
  .circle {
    display: none;
  }
  .content {
    color: #6e6d6d;
    font-size: 27px;
    font-family: YekanBakh;
    margin: 0;
    font-weight: 300;
    transition: color 0.6s;
  }
  &.active {
    .checkbox {
      background: #ff4d4d;
      border-color: #ff4d4d;
      z-index: 10;
    }
    .circle {
      display: block;
      position: absolute;
      width: 39px;
      height: 39px;
      border-radius: 39px;
      background-color: #f5f5f5;
      right: -9px;
    }
    .content {
      color: #ff4d4d;
      font-weight: 500;
    }
  }
`;

const SecondRadioButton = styled(FirstRadioButton)`
  .content {
    font-size: 24px;
    color: #000000;
  }
  .checkbox {
    border: 1px solid #000000;
    width: 23px;
    height: 23px;
    border-radius: 23px;
    display: flex;
  }
  &.active {
    .checkbox {
      background: inherit;
      position: relative;
      &:before {
        content: "";
        display: block;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        background-color: #ff4d4d;
        width: 11px;
        height: 11px;
        border-radius: 11px;
      }
    }
  }
`;

//data input style
const PersianDatePicker = styled.div`
  div {
    div {
      & > .rmdp-input {
        width: 480px;
        border-color: #ff4d4d;
        border-radius: 6px;
        color: #ff4d4d;
        font-size: 22px;
        font-weight: 400;
        font-family: "YekanBakh";
        padding: 4px 20px;
        // background-color:#F5F5F5;
      }
      & > .rmdp-input:focus {
        background-color: inherit;
      }

      & > svg {
        left: 94% !important;
        color: #ff4d4d;
      }
    }

    & > .rmdp-shadow {
      box-shadow: none;
      width: 520px;
      border: 1px solid #ff4d4d;
      border-bottom: none;
      border-radius: 6px 6px 0 0;
      transform: translateY(6px);
    }

    .rmdp-calendar {
      width: 100%;
      .rmdp-header {
        direction: ltr;
        padding: 10px 30px;
        div {
          .rmdp-header-values {
            margin: 0;
            color: #ff4d4d;
            font-size: 30px;
            font-family: "YekanBakh";
            span:nth-child(2) {
              display: none;
            }
          }
          .rmdp-right {
            left: 80%;
            .rmdp-arrow {
              border-color: #344655;
            }
          }

          .rmdp-left {
            right: 8px;
            &:after {
              content: "";
              position: absolute;
              width: 86px;
              height: 32px;
              border-radius: 16px;
              border: 1px solid #ff4d4d;
              top: -7px;
              right: -10px;
            }
            &:before {
              content: "";
              position: absolute;
              border-left: 1px solid #ff4d4d;
              height: 11px;
              width: 1px;
              top: 50%;
              left: -14px;
              transform: translate(0, -50%);
            }
            .rmdp-arrow {
              border-color: #344655;
            }
          }
        }
      }
    }
    .rmdp-day-picker {
      div {
        width: 100%;
        .rmdp-week {
          .rmdp-week-day {
            color: #04165d;
            font-size: 17px;
            font-family: "YekanBakh";
          }
        }
      }
    }
  }

  .rmdp-arrow-container:hover {
    background-color: inherit;
  }

  .rmdp-selected span {
    width: 50%;
    margin: auto;
    border-radius: 6px;
  }
  .rmdp-day.rmdp-today span {
    width: 50%;
    margin: auto;
    border-radius: 6px;
  }

  .rmdp-day:not(.rmdp-disabled):not(.rmdp-day-hidden) span:hover {
    width: 50%;
    margin: auto;
    border-radius: 6px;
  }
`;
