import React, { useState } from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Field(props) {
  const component = props.component;
  const label = props.label;
  const title = props.title;
  const name = props.name;
  const hint = props.hint;
  const disabled = props.disabled;
  const required = props.required;
  const defaultValue = props.defaultValue;
  const prefix = props.prefix;
  const suffix = props.suffix;
  const type = props.type;
  const color = props.color;
  const gradeint = props.gradeint;

  // text button function
  function textButton({ props }) {
    return (
      <TXButton
        color={props.color}
        gradeint={props.gradeint}
        text={props.label}
      >
        {props.prefix ? <FontAwesomeIcon icon={props.prefix} /> : ""}
        <p>{props.label}</p>
        {props.suffix ? <FontAwesomeIcon icon={props.suffix} /> : ""}
      </TXButton>
    );
  }

  //select component function
  function SelectButton({ props }) {
    const [open, setOpen] = useState(false);
    const [option, setOption] = useState("");
    const options = [
      "Azure Female",
      "Iron Dwarf",
      "Highborn Human",
      "Lowland Human",
      "Mountain Dwarf",
      "Scythian Elf",
      "Woodland Elf",
    ];

    const handleClick = () => {
      if (!open) {
        setOpen(true);
      } else {
        setOpen(false);
      }
    };

    const handleText = (ev) => {
      setOption(ev.currentTarget.textContent);
    };

    const itemList = (props) => {
      const list = props.map((item) => (
        <div
          onClick={handleText}
          className="dropdown__item"
          key={item.toString()}
        >
          {item}
        </div>
      ));

      return <div className="dropdown__items"> {list} </div>;
    };

    return (
      <DropDown className={open ? "active" : ""} onClick={handleClick}>
        <div className="dropdown__text">
          {!option ? "انتخاب کن..." : option}
        </div>
        {itemList(options)}
      </DropDown>
    );
  }

  //   generate origin component by components props
  function checkComponents() {
    if (component === "text") {
      return textButton({ props });
    } else if (component === "select") {
      return SelectButton({ props });
    }
  }

  return checkComponents();
}

// text button styled
const TXButton = styled.div`
  border: 1px solid ${(props) => (props.color ? props.color : "none")};
  border-radius: 20px;
  padding: ${(props) =>
    props.text === "لغو" ? "4px 70px 0px" : "4px 30px 0px"};
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 20px;
  width: fit-content;
  background: ${(props) =>
    props.gradeint
      ? `transparent linear-gradient(90deg, ${props.gradeint.slice(
          0,
          7
        )} 0%, ${props.gradeint.slice(8, 15)} 100%) 0% 0% no-repeat padding-box`
      : ""};

  p {
    font-size: 24px;
    font-family: YekanBakh;
    color: ${(props) => (props.color ? props.color : "white")};
    margin: 0;
    font-weight: 500;
  }

  svg {
    color: ${(props) => (props.color ? props.color : "white")};
  }
`;

// select component styled
export const Option = styled.div``;

const DropDown = styled.div`
  position: relative;
  background: #f5f5f5 0% 0% no-repeat padding-box;
  border: 1px solid #37b3b8;
  border-radius: 18px;
  padding: 2.5px 25px 2.5px 10px;

  .dropdown__text {
    color: #37b3b8;
    cursor: pointer;
    font-family: YekanBakh;
    font-size: 20px;
    text-align:end;
  }

  .dropdown__text:before{
    position:relative;
    content:'';
    display:block;
    border: solid #37b3b8;
    border-width: 0 1px 1px 0;
    padding: 6px;
    left: 15px;
    top: 50%;
    transform: translate(0, -60%) rotate(45deg);

  }


//   .dropdown__text:before {
//     content: "";
//     transition: all 0.3s;
//     border: solid #37b3b8;
//     border-width: 0 1px 1px 0;
//     float: left;
//     margin-top: 5px;
//     margin-right: 35px;
//     padding: 6px;
//     transform: rotate(45deg);
//     -webkit-transform: rotate(45deg);
//   }

  &.active {
    .dropdown__text:before {
      animation: rotate-arrow 2s;
      transform: rotate(-135deg);
      -webkit-transform: rotate(-135deg);
    }
  }

//   &.active {
//     .dropdown__text:after {
//         content: "";
//         border: solid #37b3b8;
//         border-width: 0 1px 1px 0;
//         float: left;
//         margin-top: 5px;
//         margin-right: 35px;
//         padding:6px;
//         transform: rotate(-135deg);
//         -webkit-transform: rotate(-135deg);
//         animation: fade-in 1s ;
//     }
//   }

  .dropdown__items {
    position: relative;
    visibility: hidden;
    opacity: 0;
    max-height: 0px;
    transition: max-height 0.6s ease;
  }

  &.active {
    .dropdown__items {
      visibility: visible;
      border-top: 1px solid #666;
      height: auto;
      max-height: 290px;
      opacity: 1;
      transition: max-height 0.7s, opacity 3s, visibility 4s ease;
    }
  }

  .dropdown__item {
    cursor: pointer;
    padding: 10px 18px;
  }

  @keyframes rotate-arrow {
    0% {
      padding: 7px;
    }
    25%{
        padding:3.5px;
    }
    50%{
        padding:0px;
    }
    50%{
        padding:0px;
        transform: rotate(-135deg);
         -webkit-transform: rotate(-135deg);
    }
    75%{
        padding:3px
    }
    90% {
      padding: 7px;
    }
    100%{
        padding:6px
    }
  }
